widget.require:
        PropertyTester = function() {};
        PropertyTester.prototype.onLoad = function(){
          document.getElementById("__WID__date").value = this.OpenAjax.getPropertyValue( "subscribedDate" );
        };

        PropertyTester.prototype.handleClick = function(){
//          alert("click!");
        };

        PropertyTester.prototype.handleChange = function(){
          // publish any changes to the input value by changing the property
          // value above that has publish="true"
          this.OpenAjax.setPropertyValue( "publishedDate", document.getElementById("__WID__date").value );
        };

        PropertyTester.prototype.onChangeSubscribedDate = function( event ){
          document.getElementById("__WID__date").value = event.newValue;
        };
