widget.require:

var oaa_sub = function() {};
oaa_sub.prototype = {
    onLoad: function() {
        var v = [ "foo", "id1", "bar", "baz", "id2" ];
        for ( var i = 0; i < v.length; i++ ) {
            var e = document.getElementById( "__WID__" + v[i] );
            e.innerHTML = v[i] + " = " + eval( v[i] );
        }
    }
};
