widget.javascript:
    (function() {
        var updating = false;

        var cal1 = new YAHOO.widget.Calendar("__WID__cal1Container"); 
        cal1.render();

        cal1.selectEvent.subscribe( function( type, args, obj ) {
                if ( updating ) {
                    return;
                }
                var d = args[0][0];
                var date = new Date( d[0], d[1] - 1, d[2] );
                updating = true;
                OpenAjax.widget.byId("__WID__").OpenAjax.setPropertyValue( "date", date );
                updating = false; 
        });

        OpenAjax.widget.byId("__WID__").onChangeDate = function( event )
        {
            if ( updating ) {
                return;
            }

            updating = true;
            cal1.select( event.newValue );
            updating = false;

	        var selectedDates = cal1.getSelectedDates(); 
	        if (selectedDates.length > 0) { 
	            var firstDate = selectedDates[0]; 
	            cal1.cfg.setProperty("pagedate", (firstDate.getMonth()+1) + "/" + firstDate.getFullYear()); 
	            cal1.render(); 
	        } else { 
	            alert("Cannot select the date" + event.newValue.toDateString()); 
	        } 
        };
    })();
